def add(no1,no2):
	ret = no1+no2                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
	return(ret)

def main():
	no1=int(input("Enter first no"))
	no2=int(input("Enter second no"))
	ans=add(no1,no2)
	print("The addition is:",ans)
	

if __name__ == '__main__':
		main()	
